import React, { useEffect } from "react";
import "bootstrap/dist/css/bootstrap.css";
import { useState } from "react";
import { Tooltip } from "react-tooltip";

export default function Todos() {
  const [todoHeadline, setTodoHeadline] = useState("");
  const [todoDescription, setTodoDescription] = useState("");
  const [headlineError, setHeadlineError] = useState("");
  const [descriptionError, setDescriptionError] = useState("");
  const [id, setId] = useState(0);
  const [editTodoId, setEditTodoid] = useState(0);
  const [actionMessage, setActionMessage] = useState("");
  const [msgColor, setMsgColor] = useState("");
  const [todos, setTodos] = useState([]);
  const [isEditClicked, setIsEditClicked] = useState(false);
  const [editedHeadline, setEditedHeadline] = useState(todoHeadline);

  function addTask(headline, description) {
    if (headline == "" || description == "") {
      if (description == "") {
        setDescriptionError("Please enter description...");
      }
      if (headline == "") {
        setHeadlineError("Please enter a headline...");
      }
    } else {
      setActionMessage("Task Added !!!");
      setId(id + 1);
      const newTask = {
        id,
        headline,
        description,
      };
      setTodos([newTask, ...todos]);
      setHeadlineError("");
      setDescriptionError("");
      setMsgColor("green");
      setTimeout(() => {
        setActionMessage("");
      }, 2000);
    }

    // setTodoHeadline("");
    // setTodoDescription("");
  }

  function deleteTask(id) {
    setId(id);
    setTimeout(() => {
      setTodos(todos.filter((todo) => todo.id !== id));
    }, 500);
    setActionMessage("Task Deleted !!!");
    setMsgColor("red");
    setTimeout(() => {
      setActionMessage("");
    }, 3000);
  }

  function editTask(todoId) {
    setEditTodoid(todoId);
    setIsEditClicked(todoId == editTodoId);
  }

  function saveHeadline(id) {
    const todo = todos.find((todo) => todo.id == id);
    todo.headline = editedHeadline;
    todos[id] = todo;
    setTodos(todos);
    setIsEditClicked(false);
    setMsgColor("blue");
    setActionMessage("Task Edited !!!");
    setTimeout(() => {
      setActionMessage("");
    }, 3000);
  }

  return (
    <>
      <div className="container">
        <div className="text-center fs-1 bg-dark text-white rounded p-3">
          Todo App
        </div>
        <div className="p-4">
          <div class="mb-3">
            <label for="exampleInputEmail1" class="form-label text-start">
              Task headline...
            </label>
            <span className="mx-4 text-danger">{headlineError}</span>
            <input
              type="text"
              placeholder="write your task headline..."
              class="form-control"
              id="exampleInputEmail1"
              aria-describedby="emailHelp"
              value={todoHeadline}
              onChange={(e) => setTodoHeadline(e.target.value)}
            />
          </div>
          <div class="mb-3">
            <label for="exampleInputPassword1" class="form-label">
              Description
            </label>
            <span className="mx-4 text-danger">{descriptionError}</span>
            <textarea
              placeholder="write your task decsciption..."
              class="form-control"
              id="exampleInputPassword1"
              value={todoDescription}
              onChange={(e) => setTodoDescription(e.target.value)}
            />
          </div>
          <div className="text-center">
            <button
              type="button"
              onClick={() => addTask(todoHeadline, todoDescription)}
              className="btn btn-primary w-75"
            >
              Add Task
            </button>
          </div>

          <div class="row justify-content-start">
            <div class="col-6 fs-3">Tasks</div>
            <div className="col-6 text-start p-4" style={{ color: msgColor }}>
              {actionMessage}
            </div>
          </div>

          <div>
            <div>
              {todos.length > 0 ? (
                <table className="table">
                  <thead>
                    <tr>
                      <th scope="col">#</th>
                      <th scope="col">Headline</th>
                      <th scope="col">Description</th>
                      <th scope="col">Handle</th>
                    </tr>
                  </thead>
                  <tbody>
                    {todos.map((todo) => (
                      <tr>
                        <th scope="row">{todo.id}</th>
                        <td>
                          {isEditClicked ? (
                            <span>
                              <input
                                value={editedHeadline}
                                onChange={(e) =>
                                  setEditedHeadline(e.target.value)
                                }
                              ></input>
                              <button
                                className="btn"
                                onClick={() => saveHeadline(todo.id)}
                              >
                                &#9745;
                              </button>
                              <button
                                className="btn"
                                onClick={() => setIsEditClicked(false)}
                              >
                                &#x2612;
                              </button>
                            </span>
                          ) : (
                            <>{todo.headline}</>
                          )}
                        </td>
                        <td>{todo.description}</td>
                        <td>
                          <Tooltip anchorSelect=".delete" place="top">
                            Delete
                          </Tooltip>
                          <a className="delete">
                            <button
                              type="button"
                              onClick={() => deleteTask(todo.id)}
                              className="btn btn-danger"
                            >
                              &#x2613;
                            </button>
                          </a>
                          <span className="p-2">
                            <Tooltip anchorSelect=".edit" place="top">
                              Edit
                            </Tooltip>
                            <a className="edit">
                              <button
                                type="button"
                                onClick={() => editTask(todo.id)}
                                className="btn btn-info"
                              >
                                &#9998;
                              </button>
                            </a>
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              ) : (
                <></>
              )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
