import React from "react";
import "bootstrap/dist/css/bootstrap.css";
import { useState } from "react";
import { Tooltip } from "react-tooltip";
import Todo from "./Todo";

export default function Todos() {
  const [todoHeadline, setTodoHeadline] = useState("");
  const [todoDescription, setTodoDescription] = useState("");

  const [todos, setTodos] = useState([
    {
      id: 0,
      headline: "meeting",
      description: "daily at 5pm",
    },
  ]);

  const [id, setId] = useState(1);

  const [actionMessage, setActionMessage] = useState("");
  const [msgColor, setMsgColor] = useState("");
  const [headlineError, setHeadlineError] = useState("");
  const [descriptionError, setDescriptionError] = useState("");

  const [editedTodoId, setEditedTodoId] = useState(0);

  const [isEditHeadlineClicked, setIsEditHeadlineClicked] = useState(false);
  const [editedHeadline, setEditedHeadline] = useState(todoHeadline);

  const [isEditDescriptionClicked, setIsEditDescriptionClicked] =
    useState(false);
  const [editedDescription, setEditedDescription] = useState(todoDescription);

  function addTask(headline, description) {
    if (headline == "" || description == "") {
      if (description == "") {
        setDescriptionError("Please enter description...");
      }
      if (headline == "") {
        setHeadlineError("Please enter a headline...");
      }
    } else {
      setActionMessage("Task Added !!!");
      setId(id + 1);
      const newTask = {
        id,
        headline,
        description,
      };
      setTodos([newTask, ...todos]);
      setHeadlineError("");
      setDescriptionError("");
      setMsgColor("green");
      setTimeout(() => {
        setActionMessage("");
      }, 2000);
    }

    // setTodoHeadline("");
    // setTodoDescription("");
  }

  function deleteTask(id) {
    setId(id);
    setTimeout(() => {
      setTodos(todos.filter((todo) => todo.id !== id));
    }, 500);
    setActionMessage("Task Deleted !!!");
    setMsgColor("red");
    setTimeout(() => {
      setActionMessage("");
    }, 3000);
    if (todos.length <= 1) {
      setId(1);
    }
  }

  function editTaskHeadline(todoId) {
    setEditedTodoId(todoId);
    // console.log("todoId : " + todoId);
    // console.log("editTodoId : " + editTodoId);
    setIsEditHeadlineClicked(true);
  }

  function saveHeadline(newHeadline, todoId) {
    console.log("todoId : " + todoId);
    console.log("editTodoId : " + editedTodoId);
    const todo = todos.find((todo) => todo.id == todoId);
    todo.headline = newHeadline;
    console.log("new todo id : " + todo.id);
    console.log("new todo id : " + todo.headline);
    console.log("new todo id : " + todo.description);
    todos[todoId] = todo;
    console.log("todos : " + todos);
    setTodos(todos);
    setIsEditHeadlineClicked(false);
    setMsgColor("blue");
    setActionMessage("Task Headline Updated !!!");
    setTimeout(() => {
      setActionMessage("");
    }, 3000);
  }

  function editTaskDescription(todoId) {
    setEditedTodoId(todoId);
    // console.log("todoId : " + todoId);
    // console.log("editTodoId : " + editTodoId);
    setIsEditDescriptionClicked(true);
  }

  function saveDescription(description, todoId) {
    const todo = todos.find((todo) => todo.id == todoId);
    todo.description = description;
    todos[todoId] = todo;
    setTodos(todos);
    setIsEditDescriptionClicked(false);
    setMsgColor("blue");
    setActionMessage("Task Description Updated !!!");
    setTimeout(() => {
      setActionMessage("");
    }, 3000);
  }

  const [toggleText, setToggleText] = useState("Dark Mode");
  const [isToggleClicked, setIsToggleClicked] = useState(false);

  let toggleButtonColor = "btn-dark";
