                            <></>
                          )}
                          {!isEditHeadlineClicked ? (
                            <span className="p-2">
                              <Tooltip
                                anchorSelect=".edit-headline"
                                place="top"
                              >
                                Edit Headline
                              </Tooltip>
                              <a className="edit-headline">
                                <button
                                  type="button"
                                  onClick={() => editTaskHeadline(todo.id)}
                                  className="btn"
                                >
                                  &#9998;
