import React from "react";
import "bootstrap/dist/css/bootstrap.css";
import { useState } from "react";
import { Tooltip } from "react-tooltip";
import Todo from "./Todo";

export default function Todos() {
  const [todoHeadline, setTodoHeadline] = useState("");
  const [todoDescription, setTodoDescription] = useState("");
  const [headlineError, setHeadlineError] = useState("");
  const [descriptionError, setDescriptionError] = useState("");
  const [id, setId] = useState(0);
  const [editedTodoId, setEditedTodoId] = useState(0);
  const [actionMessage, setActionMessage] = useState("");
  const [msgColor, setMsgColor] = useState("");
  const [todos, setTodos] = useState([]);
  const [isEditClicked, setIsEditClicked] = useState(false);
  const [editedHeadline, setEditedHeadline] = useState(todoHeadline);

  function addTask(headline, description) {
    if (headline == "" || description == "") {
      if (description == "") {
        setDescriptionError("Please enter description...");
      }
      if (headline == "") {
        setHeadlineError("Please enter a headline...");
      }
    } else {
      setActionMessage("Task Added !!!");
      setId(id + 1);
      const newTask = {
        id,
        headline,
        description,
      };
      setTodos([newTask, ...todos]);
      setHeadlineError("");
      setDescriptionError("");
      setMsgColor("green");
      setTimeout(() => {
        setActionMessage("");
      }, 2000);
    }

    // setTodoHeadline("");
    // setTodoDescription("");
  }

  function deleteTask(id) {
    setId(id);
    setTimeout(() => {
      setTodos(todos.filter((todo) => todo.id !== id));
    }, 500);
    setActionMessage("Task Deleted !!!");
    setMsgColor("red");
    setTimeout(() => {
      setActionMessage("");
    }, 3000);
  }

  function editTask(todoId) {
    setEditedTodoId(todoId);
    // console.log("todoId : " + todoId);
    // console.log("editTodoId : " + editTodoId);
    setIsEditClicked(true);
  }

  function saveHeadline(newHeadline, todoId) {
    console.log("todoId : " + todoId);
    console.log("editTodoId : " + editedTodoId);
    const todo = todos.find((todo) => todo.id == todoId);
    todo.headline = newHeadline;
    console.log("new todo id : " + todo.id);
    console.log("new todo id : " + todo.headline);
    console.log("new todo id : " + todo.description);
    todos[todoId] = todo;
    console.log("todos : " + todos);
    setTodos(todos);
    setIsEditClicked(false);
    setMsgColor("blue");
    setActionMessage("Task Edited !!!");
    setTimeout(() => {
      setActionMessage("");
    }, 3000);
  }

  return (
    <>
      <div className="container p-3">
        <Todo todos={todos}></Todo>
        <div className="text-center fs-1 border border-outline-dark border-radius-lg rounded-4 p-3">
          Todo App
        </div>
        <div className="p-4">
          <div class="mb-3">
            <label for="exampleInputEmail1" class="form-label text-start">
              Task headline...
            </label>
            <span className="mx-4 text-danger">{headlineError}</span>
            <input
              type="text"
              placeholder="write your task headline..."
              class="form-control"
              id="exampleInputEmail1"
              aria-describedby="emailHelp"
              value={todoHeadline}
              onChange={(e) => setTodoHeadline(e.target.value)}
            />
          </div>
          <div class="mb-3">
            <label for="exampleInputPassword1" class="form-label">
              Description
            </label>
            <span className="mx-4 text-danger">{descriptionError}</span>
            <textarea
              placeholder="write your task decsciption..."
              class="form-control"
              id="exampleInputPassword1"
              value={todoDescription}
              onChange={(e) => setTodoDescription(e.target.value)}
            />
          </div>
          <div className="text-center">
            <button
              type="button"
              onClick={() => addTask(todoHeadline, todoDescription)}
              className="btn btn-outline-info w-75 text-dark"
            >
              Add Task
            </button>
          </div>

          <div class="row justify-content-start">
            <div class="col-6 fs-3 ">Tasks</div>
            <div className="col-6 text-start p-4" style={{ color: msgColor }}>
              {actionMessage}
            </div>
          </div>

          <div>
            <div>
              {todos.length > 0 ? (
                <table className="table">
                  <thead>
                    <tr>
                      <th scope="col">#</th>
                      <th scope="col">Headline</th>
                      <th scope="col">Description</th>
                      <th scope="col">Handle</th>
                    </tr>
                  </thead>
                  <tbody>
                    {todos.map((todo) => (
                      <tr>
                        <td scope="row">{todo.id}</td>
                        <td>
                          {isEditClicked && editedTodoId === todo.id ? (
                            <span>
                              <input
                                className="form-control"
                                value={editedHeadline}
                                onChange={(e) =>
                                  setEditedHeadline(e.target.value)
                                }
                              />
                              <span>
                                <button
                                  className="btn text-dark btn-outline-info m-2"
                                  onClick={() =>
                                    saveHeadline(editedHeadline, editedTodoId)
                                  }
                                >
                                  Save &#x2611;
                                </button>
                                <button
                                  className="btn text-danger btn-outline-dark m-2"
                                  onClick={() => setIsEditClicked(false)}
                                >
                                  Cancel &#x2612;
                                </button>
                              </span>
                            </span>
                          ) : (
                            <>
                              {todo.headline}
                              <span className="p-2">
                                <Tooltip anchorSelect=".edit" place="top">
                                  Edit Headline
                                </Tooltip>
                                <a className="edit">
                                  <button
                                    type="button"
                                    onClick={() => editTask(todo.id)}
                                    className="btn"
                                  >
                                    &#9998;
                                  </button>
                                </a>
                              </span>
                            </>
                          )}
                        </td>
                        <td>
                          {todo.description}
                          <span className="p-2">
                            <Tooltip anchorSelect=".edit" place="top">
                              Edit Description
                            </Tooltip>
                            <a className="edit">
                              <button
                                type="button"
                                onClick={() => editTask(todo.id)}
                                className="btn"
                              >
                                &#9998;
                              </button>
                            </a>
                          </span>
                        </td>
                        <td>
                          <Tooltip anchorSelect=".delete" place="top">
                            Delete Task
                          </Tooltip>
                          <a className="delete">
                            <button
                              type="button"
                              onClick={() => deleteTask(todo.id)}
                              className="btn btn-danger px-5"
                            >
                              &#x2613;
                            </button>
                          </a>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              ) : (
                <></>
              )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
