import React, { useState } from "react";

export default function AddTodo(){

    const [todoHeadline, setTodoHeadline] = useState("");
    const [todoDescription, setTodoDescription] = useState("");

    const [todos, setTodos] = useState({
        headline : "",
        decription : ""
    });

    const allTodos = [{}];

    const handleHeadlineChange = (e) => {
        setTodoHeadline(e.target.value);
    }

    const handleHeadlineDescription = (e) => {
        setTodoDescription(e.target.value);
    }

    todos.headline=todoHeadline;
    todos.decription=todoDescription;

    const hadleSaveChange = () => {
        allTodos.push(todos);
       console.log("todos : "+todos);
       allTodos.forEach(todo => {
        console.log(todo);
        console.log("todosHead : "+todos.headline);
       console.log("todosDescr : "+todos.decription);
    });

    }

    allTodos.forEach(todo => {
        console.log("all Todos : "+todo.headline);
    });


    return(
        <>
            <button type="button" className="btn btn-success fs-5" data-toggle="modal" data-target="#exampleModalCenter">
            + Add Task
            </button>

            <div className="modal fade" id="exampleModalCenter" tabindex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
                <div className="modal-dialog modal-dialog-centered" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h3 className="modal-title mx-4" id="exampleModalLongTitle">Please add your task </h3>
                        </div>
                    <div class="modal-body">
                        <form>
                            <div class="mb-3">
                                <label for="exampleInputEmail1" class="form-label text-start">Task headline...</label>
                                <input type="text" 
                                placeholder="write your task headline..." 
                                class="form-control" id="exampleInputEmail1" 
                                aria-describedby="emailHelp"
                                value={todoHeadline}
                                onChange={handleHeadlineChange}
                                />
                            </div>
                            <div class="mb-3">
                                <label for="exampleInputPassword1" class="form-label">Description</label>
                                <textarea 
                                 placeholder="write your task decsciption..."
                                 class="form-control" id="exampleInputPassword1"
                                 value={todoDescription}
                                 onChange={handleHeadlineDescription}
                                 />
                            </div>
                            <button type="submit" class="btn btn-primary">Add</button>
                        </form>
                    </div>
                    <div className="modal-footer">
                        <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                        <button type="button" onClick={hadleSaveChange} className="btn btn-primary">Save changes</button>
                    </div>
                    </div>
                </div>
            </div>
        </>
    )
}
